// React Basic and Bootstrap
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col, Pagination, PaginationItem, PaginationLink  } from 'reactstrap';

//Import components
import PageBreadcrumb from "../../../components/Shared/page-breadcrumb";

import work1 from '../../../images/work/1.jpg';
import work2 from '../../../images/work/2.jpg';
import work3 from '../../../images/work/3.jpg';
import work4 from '../../../images/work/4.jpg';
import work5 from '../../../images/work/5.jpg';
import work6 from '../../../images/work/6.jpg';
import work7 from '../../../images/work/7.jpg';
import work8 from '../../../images/work/8.jpg';
import work9 from '../../../images/work/9.jpg';
import work10 from '../../../images/work/10.jpg';
import work11 from '../../../images/work/11.jpg';
import work12 from '../../../images/work/12.jpg';

class PageWork extends Component {

    constructor(props) {
        super(props);
        this.state = {
            pathItems : [
                //id must required
                { id : 1, name : "Home", link : "/index" },
                { id : 2, name : "Pages", link : "#" },
                { id : 3, name : "Portfolio", link : "#" },
                { id : 4, name : "Work Grid" },
            ],
            works : [
                { image : work1, title : "Shifting Perspective", author : "Thomas Brewer", date : "13th August, 2019", category : "Designing" },
                { image : work2, title : "Colors Magazine", author : "Thomas Brewer", date : "13th August, 2019", category : "Photography" },
                { image : work3, title : "Spa Cosmetics", author : "Thomas Brewer", date : "13th August, 2019", category : "Designing" },
                { image : work4, title : "Riser Coffee", author : "Thomas Brewer", date : "13th August, 2019", category : "Designing" },
                { image : work5, title : "Dancing With My Self", author : "Thomas Brewer", date : "13th August, 2019", category : "Branding" },
                { image : work6, title : "New Trends in SEO", author : "Thomas Brewer", date : "13th August, 2019", category : "Development" },
                { image : work7, title : "Spa Cosmetics", author : "Thomas Brewer", date : "13th August, 2019", category : "Branding" },
                { image : work8, title : "Riser Coffee", author : "Thomas Brewer", date : "13th August, 2019", category : "Development" },
                { image : work9, title : "Riser Coffee", author : "Thomas Brewer", date : "13th August, 2019", category : "Development" },
                { image : work10, title : "Riser Coffee", author : "Thomas Brewer", date : "13th August, 2019", category : "Development" },
                { image : work11, title : "Riser Coffee", author : "Thomas Brewer", date : "13th August, 2019", category : "Development" },
                { image : work12, title : "Riser Coffee", author : "Thomas Brewer", date : "13th August, 2019", category : "Development" },
            ],
        }
    }

    componentDidMount() {
        window.addEventListener("scroll", this.scrollNavigation, true);
    }
     // Make sure to remove the DOM listener when the component is unmounted.
     componentWillUnmount() {
        window.removeEventListener("scroll", this.scrollNavigation, true);
     }
    scrollNavigation = () => {
        var doc = document.documentElement;
        var top = (window.pageYOffset || doc.scrollTop) - (doc.clientTop || 0);
        var topnav = document.getElementById('topnav');
        if (top > 80 && topnav) {
            topnav.classList.add('nav-sticky');
        }
        else if(topnav) {
            topnav.classList.remove('nav-sticky');
        }
    }

    render() {

        return (
            <React.Fragment>
                {/* breadcrumb */}
                <PageBreadcrumb title="Works & Portfolio" pathItems = {this.state.pathItems} />
                    <div className="position-relative">
                        <div className="shape overflow-hidden text-white">
                            <svg viewBox="0 0 2880 48" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <path d="M0 48H1437.5H2880V0H2160C1442.5 52 720 0 720 0H0V48Z" fill="currentColor"></path>
                            </svg>
                        </div>
                    </div>

                <section className="section">
                    <Container>
                        <Row>
                            {
                                this.state.works.map((work, key) =>
                                    <Col lg="4" md="6" xs="12" key={key} className="mb-4 pb-2">
                                        <div className="work-container position-relative rounded">
                                            <div className="position-relative overflow-hidden">
                                                <img src={work.image} className="img-fluid rounded" alt="work" />
                                                <div className="overlay-work"></div>
                                                <div className="content">
                                                    <Link to="page-work-detail" className="title text-white d-block font-weight-bold">{work.title}</Link>
                                                    <small className="text-light">{work.category}</small>
                                                </div>
                                                <div className="client">
                                                    <small className="text-light user d-block"><i className="mdi mdi-account"></i> {work.author}</small>
                                                    <small className="text-light date"><i className="mdi mdi-calendar-check"></i> {work.date}</small>
                                                </div>
                                            </div>
                                        </div>
                                    </Col>
                                )
                            }
                            
                            <Col xs="12">
                                <Pagination className="pagination justify-content-center mb-0 list-unstyled">
                                    <PaginationItem><PaginationLink href="#" className="pr-3 pl-3 pt-2 pb-2">Prev</PaginationLink></PaginationItem>
                                    <PaginationItem active><PaginationLink href="#" className="pr-3 pl-3 pt-2 pb-2">1</PaginationLink></PaginationItem>
                                    <PaginationItem><PaginationLink href="#" className="pr-3 pl-3 pt-2 pb-2">2</PaginationLink></PaginationItem>
                                    <PaginationItem><PaginationLink href="#" className="pr-3 pl-3 pt-2 pb-2">3</PaginationLink></PaginationItem>
                                    <PaginationItem><PaginationLink href="#" className="pr-3 pl-3 pt-2 pb-2">Next</PaginationLink></PaginationItem>
                                </Pagination>
                            </Col>
                        </Row>
                    </Container>
                </section>
            </React.Fragment>
        );
    }
}
export default PageWork;
