// React Basic and Bootstrap
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'reactstrap';

//Import Components
import Feature from "../../components/Shared/Feature";


// Import images
import insurance from "../../images/icon/insurance.svg";
import graduationHat from "../../images/icon/graduation-hat.svg";
import ai from "../../images/icon/ai.svg";
import github from "../../images/icon/github.svg";



class Products extends Component {

  constructor(props) {
       super(props);
       this.state = {
           features : [
               { icon : github, title : "Fraktal Network", desc : "Send money instantly, anywhere in the world without bank fees.", link : "#" },
               { icon : graduationHat, title : "Our Courses", desc : "It is a long established fact that a reader will be of a page reader will be of a page when looking at its layout.", link : "#" },
               { icon : ai, title : "Expert Teachers", desc : "It is a long established fact that a reader will be of a page reader will be of a page when looking at its layout.", link : "#" },
                  { icon : github, title : "Expert Teachers", desc : "It is a long established fact that a reader will be of a page reader will be of a page when looking at its layout.", link : "#" },
           ]
       }
   }



    render() {
        return (
            <React.Fragment>
                <section className="section pt-0">
                <div className="benefitsTitle">
                <h4 className="title mb-4">We've Built some Very Impactful Products</h4>
                </div>

                <Container>
                       <Row>
                           {
                               this.state.features.map((feature, key) =>
                                   <Col md={3} key={key}>
                                       <div className="course-feature position-relative d-block overflow-hidden bg-white rounded p-4 pt-5 pb-5">
                                           <div className="icon">
                                               <img src={feature.icon} className="avatar avatar-small" height="55" alt="" />
                                           </div>
                                           <h4 className="mt-3 text-center"><Link to={feature.link} className="title text-dark"> {feature.title}</Link></h4>
                                           <p className="text-muted">{feature.desc} </p>

                                           <img src={feature.icon} className="full-img" height="300" alt="" />
                                       </div>
                                   </Col>
                               )
                           }
                       </Row>
                   </Container>

                </section>
            </React.Fragment>
        );
    }
}

export default Products;
