import React, { Component } from 'react';
import { Container } from "reactstrap";

//Import Components
import Intro from "./intro";
import Partners from "./partners";


//Import Images


class IndexclassNameicSaas extends Component {
    state = {

        facilities : [
            { icon : "shield" ,title : "Fully Secured", desc : "Moreover, in Latin only words at the beginning of sentences are capitalized."},
            { icon : "cpu", title : "Best Performance", desc : "If the fill text is intended to illustrate the characteristics of sometimes."},
        ],
    }

    componentDidMount() {
        document.body.classList = "";
        document.getElementById("buyButton").classList.remove("btn-primary");
        document.getElementById("buyButton").classList.add("btn-light");

        var featurebox = document.getElementsByName("featurebox");
        for (var i=0; i<featurebox.length; i++){
            if( i !== 0){
                featurebox[i].classList.add("mt-5");
                featurebox[i].classList.add("mt-sm-0");
            }
        }
        window.addEventListener("scroll", this.scrollNavigation, true);
      }
       // Make sure to remove the DOM listener when the component is unmounted.
    componentWillUnmount() {
        window.removeEventListener("scroll",this.scrollNavigation, true);
     }

      scrollNavigation = () => {
          var doc = document.documentElement;
          var top = (window.pageYOffset || doc.scrollTop)  - (doc.clientTop || 0);
          if(top > 80)
          {
               document.getElementById("buyButton").classList.remove("btn-light");
               document.getElementById("buyButton").classList.add("btn-primary");
               document.getElementById('topnav').classList.add('nav-sticky');
          }
          else
          {
            document.getElementById("buyButton").classList.remove("btn-primary");
            document.getElementById("buyButton").classList.add("btn-light");
            document.getElementById('topnav').classList.remove('nav-sticky');
          }
      }
    render() {
        return (
            <React.Fragment>
                {/* render section */}
                <Section/>

                <section className="pt-5">
                    <Container>
                        {/* render partner */}
                        <Partners/>
                    </Container>
                </section>

                <section className="section overflow-hidden">
                    <Container className="pb-5 mb-md-5">
                        {/* feature box */}
                        <Feature featureArray={this.state.features} isCenter={true} />
                    </Container>

                    {/* Render Feature Description */}
                    <FeatureDescription image1={classic02} image2={classic02} testimonials={this.state.testimonials} facilities={this.state.facilities} />

                    {/* Render CTA */}
                    <CTA/>

                    {/* render pricing */}
                    <Pricings pricings={this.state.pricings} />

                    {/* render trial */}
                    <Trial/>
                </section>
            </React.Fragment>
        );
    }
}

export default IndexclassNameicSaas;
