// React Basic and Bootstrap
import React, { Component } from 'react';
import { Container } from 'reactstrap';

//Import Components
import SectionTitle from "../../components/Shared/section-title";
import ReviewsSlider from "../../components/Shared/reviews-slider";

//Import Images



class Testi extends Component {

    componentDidMount(){
        var sectiontitles = document.getElementsByName("maintitle");
        sectiontitles[sectiontitles.length-1].classList.remove("mb-4");
        sectiontitles[sectiontitles.length-1].classList.remove("pb-2");
        sectiontitles[sectiontitles.length-1].classList.add("mb-60");
    }

    constructor(props) {
        super(props);
        this.state = {
            reviews : [
                { id : 1,  name : "Thomas Israel", post : "C.E.O", desc : "It seems that only fragments of the original text remain in the Lorem Ipsum texts used today.", rating : 5 },
                { id : 2,  name : "Barbara McIntosh", post : "M.D", desc : "One disadvantage of Lorum Ipsum is that in Latin certain letters appear more frequently than others.", rating : 4 },
                { id : 3,  name : "Carl Oliver", post : "P.A", desc : "The most well-known dummy text is the 'Lorem Ipsum', which is said to have originated in the 16th century.", rating : 3 },
                { id : 4,  name : "Christa Smith", post : "Manager", desc : "According to most sources, Lorum Ipsum can be traced back to a text composed by Cicero.", rating : 5 },
                { id : 5,  name : "Dean Tolle", post : "Developer", desc : "There is now an abundance of readable dummy texts. These are usually used when a text is required.", rating : 5 },
                { id : 6,  name : "ill Webb", post : "Designer", desc : "Thus, Lorem Ipsum has only limited suitability as a visual filler for German texts.", rating : 4 },
            ],
        }
    }

    render() {
        return (
            <React.Fragment>
                <section className="section bg-light">
                    <Container className="pb-lg-4 mb-md-5 mb-4">
                        {/* section title */}
                        <SectionTitle title="Client Reviews" desc=" that can provide everything you need to generate awareness, drive traffic, connect." />

                        {/* clients slider */}
                        <ReviewsSlider reviews={this.state.reviews}/>
                    </Container>
                </section>
                <div className="position-relative">
                    <div className="shape overflow-hidden text-footer">
                        <svg viewBox="0 0 2880 250" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M720 125L2160 0H2880V250H0V125H720Z" fill="currentColor"></path>
                        </svg>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Testi;
