// React Basic and Bootstrap
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'reactstrap';


// Import images
import aboutimage from '../../images/illustrator/enterprise.png';

class Vision extends Component {


    render() {
        return (
            <React.Fragment>
                    <section className="section">
                        <Container>
                            <Row className="align-items-center">
                                <Col md={{size:6, order:1}} xs={{order:2}} className="mt-4 mt-sm-0 pt-20 pt-sm-0">
                                    <div className="section-title mr-lg-5">
                                      {/*  <h4 className="title mb-4">Our Vision</h4> */}
                                        <p className="text-muted">At Fraktal we are working towards a future where everyone has equal opportunity to succeed. Where the world operates in an open and transparent fashion and everyone is included.</p>
                                          <p className="text-muted">We believe that everyone should have an equal opportunity to succeed and it should not matter who you know, where you were born, or how much money you have.</p>
                                        <Link to="#" className="btn btn-outline-primary">Start Now <i className="mdi mdi-chevron-right"></i></Link>
                                    </div>
                                </Col>

                                <Col md={{size:6, order:2}} xs={{order:1}}>
                                    <img src={aboutimage} className="img-fluid" alt="" />
                                </Col>
                            </Row>
                        </Container>


                    </section>
            </React.Fragment>
        );
    }
}

export default Vision;
